import React, {Component} from "react"

import Burger from "../../components/Burger/Burger"
import BuildControls from "../../components/Burger/BuildControls/BuildControls"

import Aux from "../../aux/aux"

const INGREDIENT_PRICE = {
    meat: 20,
    bacon: 10,
    cheese: 10,
    salad: 5
}

class BurgerBuilder extends Component{

    state =  {
        ingredient : {
            meat: 1,
            bacon:1,
            cheese: 1,
            salad: 1,
        },
        totalPrice : 20
    }

    addIngredients = type => {

        let oldIngredientCount = this.state.ingredient[type]
        let newIngredientCount = oldIngredientCount++
        let updatedIngredients = {...this.state.ingredient}
        updatedIngredients[type] = newIngredientCount

        let newPrice = INGREDIENT_PRICE[type]
        let updatedPrice = this.state.totalPrice
        updatedPrice += newPrice

        this.setState({
            ingredient : updatedIngredients,
            totalPrice: updatedPrice
        })

    }

    render(){
        return(
            <Aux>
                <Burger ingredients={this.state.ingredient}/>
                <BuildControls addIngredients={this.addIngredients}/>
            </Aux>
        )
    }
}

export default BurgerBuilder
