const Burger = props => {
    /* const transformedIngredients  = Object.keys(props.ingredients)
                                          .map( iKey => [...Array(iKey)]
                                          .map((_,index)=> <BurgerIngredient key={iKey+index} type={iKey}/>) )
 */
