import React from "react"

import classes from "./Modal.module.css"

const Modal = props => (
    <div 
        className={classes.Modal}
        style={{
            transform: props.popUpState ? 'translateY(0)' : 'translateY(-100vh)',
            opacity: props.popUpState ? '1' : '0'
        }}
    >
        {props.children}
    </div>
)

export default Modal
